      <CountryInfo data={countries[i]}/>
    };*/

    //{top15countries.map(country => (<CountryInfo data=top15countries[]))}

  return (
    <div className="App">

    {top15countries.map(country => (<CountryInfo key= {country.cca3} data={country}/>))}
    //country är all data som finns om ett land

    </div>

  );
}

export default App;
